import axios from 'axios'
const ENDPOINT = '//api.openweathermap.org/data/2.5/weather';
const API_KEY = 'bd84d98d30841d5669d443eac854e864'

export const getWeather = async (data) => {

  const params = {
    appid: API_KEY,
    lang: 'it',
    units: 'metric',
    lat: data.lat,
    lon: data.long
  };

  return {
    "coord": {
        "lon": 10.8558,
        "lat": 45.4097
    },
    "weather": [
        {
            "id": 800,
            "main": "Clear",
            "description": "cielo sereno",
            "icon": "01n"
        }
    ],
    "base": "stations",
    "main": {
        "temp": 11.65,
        "feels_like": 9.78,
        "temp_min": 11.11,
        "temp_max": 12,
        "pressure": 1028,
        "humidity": 71
    },
    "visibility": 10000,
    "wind": {
        "speed": 1.54,
        "deg": 0
    },
    "clouds": {
        "all": 0
    },
    "dt": 1617146438,
    "sys": {
        "type": 1,
        "id": 6776,
        "country": "IT",
        "sunrise": 1617166717,
        "sunset": 1617212550
    },
    "timezone": 7200,
    "id": 3166410,
    "name": "AAA",
    "cod": 200
  }

  // return await axios.get(ENDPOINT, { params }).then(res => {
    // return res.data
  // })
}
