import { useState } from 'react';
import WeatherIcon from 'react-icons-weather';
import Moment from 'react-moment';
import '../css/Forecasts.css'

const Forecasts = ({forecast}) => {
  const [openClass, setOpenClass] = useState()

  const ForecastBoxes = () => {
    return(
      forecast.list.map(forecastObj => (
        <div className='box'>
          <span><WeatherIcon name="owm" iconId={forecastObj.weather[0].id.toString()} flip="horizontal" /></span>
          <span><Moment date={new Date(forecastObj.weather[0].dt_txt)} format="DD/MM, HH:MM" /></span>
          <span> alle </span>
          <span><Moment date={forecastObj.weather[0].dt_txt} format="" /></span>
          <p>{forecastObj.weather[0].description}</p>
        </div>
      ))
    )
  }

  return (
    <div className={'container-forecasts '+openClass}>
      <div className='contain'>
        {(Object.keys(forecast).length > 0) ?
          <ForecastBoxes />
          : ''
        }
      </div>
      <div className='side'>
        <div className="hamburger" onClick={() => setOpenClass((openClass) ? '' : 'open')}>
          <div className="hamburger-box">
            <div className="hamburger-inner"></div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Forecasts;
