    fetchDataForecast({
      lat: latitude,
      long: longitude
    })
  }

  const fetchDataForecast = async (data) => {
    const resWeather = await getForecast(data)
    setWeather(resWeather)
    console.log(weather)
  }

  return (
    <div>

      {/* <AnimationText text='Posizione attuale' /> */}
      <Forecasts />
