import axios from 'axios'
const endpoint = '//api.openweathermap.org/data/2.5/weather';
const API_KEY = 'bd84d98d30841d5669d443eac854e864'
//   const params = {
//     appid: key,
//     lang,
//     units: unit,
//     lat,
//     lon,
//   };


export const getForecast = async () => {
  // const params = {
  //   appid: API_KEY,
  //   lang,
  //   units: unit,
  //   lat,
  //   lon,
  // };

  const params = {
    appid: API_KEY,
    q:'London',
    lang: 'it',
    units: 'metric'
  };

  return await axios.get(endpoint, { params }).then(res => {
    return res
  })
}
