import React, { useEffect, useState } from 'react'
import Forecasts from '../components/Forecasts'
import { getWeather } from '../api/openweather'
import { usePosition } from 'use-position'
import Weather from '../components/Weather';

const HomePage = () => {
  const {latitude, longitude, error} = usePosition();
  const [weather, setWeather] = useState({})

  const fetchDataWeather = async (data) => {
    const resWeather = await getWeather(data)
    setWeather(resWeather)
  }

  //ATTENZIONE
  if(latitude && longitude && Object.keys(weather).length === 0){
    fetchDataWeather({
      lat: latitude,
      long: longitude
    })
  }

  const WeatherComponent = () => {
    return (weather) ?
      <Weather weather={weather} />
    : 
      <p>loading</p> 
  }

  return (
    <div>
      <WeatherComponent />
      {/* <AnimationText text='Posizione attuale' /> */}
      <Forecasts />
    </div>
  )
}
export default HomePage
