import '../css/Forecasts.css'

const Weather = ({weather}) => {
  const [openClass, setOpenClass] = useState()

  return (
    <div className='container-weather' >
      <h1>{weather.name}</h1>
      <WeatherIcon name="owm" iconId="200" flip="horizontal" rotate="90" />
    </div>
  );
}

export default Weather;
