import { useState } from 'react';
import WeatherIcon from 'react-icons-weather';
import '../css/Forecasts.css'

const Forecasts = ({forecast}) => {
  const [openClass, setOpenClass] = useState()

  const ForecastBoxes = () => {
    const forecastObj = forecast.list[0]
    const description = forecastObj.weather[0].description
    const icon = forecastObj.weather[0].id.toString()
    return(
      <div className='box'>
        <WeatherIcon name="owm" iconId={icon} flip="horizontal" /> 
        <p>{description}</p>
      </div>
    )

  }

  return (
    <div className={'container-forecasts '+openClass}>
      <div className='contain'>
        {(Object.keys(forecast).length > 0) ?
          <ForecastBoxes />
          : ''
        }
      </div>
      <div className='side'>
        <div className="hamburger" onClick={() => setOpenClass((openClass) ? '' : 'open')}>
          <div className="hamburger-box">
            <div className="hamburger-inner"></div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Forecasts;
