import React, { useEffect } from 'react'
import AnimationText from '../components/AnimationText'
import Forecasts from '../components/Forecasts'
import GetLocation from '../components/GetLocation'
import { getForecast } from '../api/openweather'

const HomePage = () => {

  const fetchDataForecast = async () => {
    const {latitude, longitude, error} = await GetLocation();
    const resForecast = await getForecast({
      latitude: latitude,
      longitude: longitude
    })
    console.log(resForecast)
  }

  useEffect( () => {
    fetchDataForecast()
  }, [])

  return (
    <div>

      {/* <AnimationText text='Posizione attuale' /> */}
      <Forecasts />
    </div>
  )
}
export default HomePage
