import { useEffect, useState } from 'react';
import WeatherIcon from 'react-icons-weather';
import AnimationText from '../components/AnimationText'
import Moment from 'react-moment';
import '../css/Weather.css'

const Weather = ({weather}) => {
  const [icon] = useState(weather.weather[0].id.toString())
  const [description] = useState(weather.weather[0].description)
  const [temp] = useState(Math.floor(weather.main.temp))
  const [lastWeatherUpdate, setlastWeatherUpdate] = useState()

  useEffect(() => {
    const lastUpdate = localStorage.getItem('lastWeatherUpdate');
    setlastWeatherUpdate(JSON.parse(lastUpdate));
  }, [])


  return (
    <div className='container-weather' >
      <div className='contain'>
        <div className='first'>
          <WeatherIcon name="owm" iconId={icon} flip="horizontal" />
          <span>{temp}°C</span>
        </div>
        <div className='second'>
          <span>Oggi a</span>
          <strong> <AnimationText text={weather.name} /> </strong>
