          <strong> <AnimationText text={description} /> </strong>
        </div>
        <div className='second'>
          <span> <Moment date={lastWeatherUpdate} format="DD/MM/YYYY" /></span>
        </div>
      </div>
    </div>
  );
}

export default Weather;
