import React, { useEffect, useState } from 'react'
import { Form, Button, Col} from 'react-bootstrap';
import { Hint } from 'react-autocomplete-hint';
import { getJsonCities } from '../../api/cities'

const CityForm = ( { handleSubmit, form, setForm } ) => {
  const [errors, setErrors] = useState({})
  const [hintData, setHintData] = useState([])
  const [text, setText] = useState('')
  const [coord, setCoord] = useState({
    lat: '',
    lon: '',
  })

  const insertCoord = (coord) => {
    setCoord(coord)
    form.lat = coord.lat
    form.lon = coord.lon
    setForm(form)
  }

  const getData = async (name) => {
    const resJsonCities = await getJsonCities(name)
    var hintArray = []
    resJsonCities.map(a => {
      hintArray.push(a.name)
      insertCoord(a.coord)
    })
    setHintData(hintArray)
  }

  const validateForm = (form) => {
    //validate handle react / react hook / yup / ..

    if(coord.lat){
      form.lon = coord.lon
      setForm(form)
    }

    if(coord.lon){
      form.lon = coord.lon 
      setForm(form)
    }

    for (const [key, value] of Object.entries(form)) {
      if(!value){
        setErrors({ [key]: "required*"})
        return false
      } 
    }

    return true
  }

  const handleChange = (target) => {
    if(target.name == 'name'){
      setText(target.value)
      if(target.value.length > 3){
        getData(target.value)
      }
    }
    setForm({
      ...form,
      [target.name]: target.value
    })
  }

  const submitForm = e => {
    e.preventDefault();
    if(validateForm(form)){
      handleSubmit(e, {...form})
    }
  }

  return(
    <Form style={{marginTop: '50px'}}>
      <Form.Row>
        <Form.Group as={Col} md={4} xs={12} controlId="GridName">
          <Form.Label>Nome Citt√†*</Form.Label>
          <Hint options={hintData} allowTabFill>
            <input className='input-with-hint form-control'
              value={text}
              name="name"
              onChange={e => handleChange(e.target)}   
              />
          </Hint>

          <p>{errors.name}</p>
        </Form.Group>
        <Form.Group as={Col} md={4} xs={12} controlId="GridLat">
          <Form.Label>Latitudine*</Form.Label>
          <Form.Control placeholder="Latitudine" name="lat" value={coord.lat} 
          disabled
          onChange={e => handleChange(e.target)}
           />
          <p>{errors.lat}</p>
        </Form.Group>
        <Form.Group as={Col} md={4} xs={12} controlId="GridLon">
          <Form.Label>Longitudine*</Form.Label>
          <Form.Control placeholder="Longitudine" name="lon" value={coord.lon} 
          disabled
          onChange={e => handleChange(e.target)}
           />
          <p>{errors.lon}</p>
        </Form.Group>
      </Form.Row>
      <Button variant="primary" type="button" onClick={submitForm}>
        Salva
      </Button>
    </Form>  
    )

}

export default CityForm
