    const resForecast = await getForecast(data)
    console.log(resForecast)
  }

  useEffect( () => {
    console.log(latitude)
    console.log(longitude)
    fetchDataForecast({
      lat: '45.409673999999995',
      long: '10.855770699999999'
    })
  }, [])

  return (
    <div>

      {/* <AnimationText text='Posizione attuale' /> */}
      <Forecasts />
    </div>
  )
}
export default HomePage
