import React, { useEffect, useState } from 'react'
import Forecasts from '../components/Forecasts'
import { getWeather } from '../api/openweather'
import Weather from '../components/Weather';

const HomePage = () => {
  const [weather, setWeather] = useState({})
  const [message, setMessage] = useState('Carico..')

  const fetchDataWeather = async (data) => {
    const resWeather = await getWeather(data)
    setWeather(resWeather)
    console.log(weather)
  }

  useEffect(()=>{
    setInterval(() => {
      console.log(111)
    }, 1000)
    setInterval(() => {
      if (navigator.geolocation){
        navigator.geolocation.watchPosition(function(position){
          fetchDataWeather({
            lat: position.coords.latitude,
            long: position.coords.longitude,
          })
        }, function(err) {
          setMessage((err.code == 1) ? 'Attiva la posizione del browser' : err.message)
        }, {
          enableHighAccuracy: true,
          timeout: 5000,
          maximumAge: 0
        });
      }
    }, 1000)
  },[])

  return (
    <div>
      {(Object.keys(weather).length > 0) ?
        <Weather weather={weather} />
        :
        <span className='message'>{message}</span>
      }
      <Forecasts />
    </div>
  )
}
export default HomePage
