import { Image, Table } from "react-bootstrap";
import { Link } from "react-router-dom";
import Moment from 'react-moment';


const TableList = ({ cities, removeCity }) => {

  return (
    <Table striped bordered hover responsive>
      <thead>
        <tr>
          <th>Nome città</th>
          <th>Longitudine</th>
          <th>Latitudine</th>
          <th>Azioni</th>
        </tr>
      </thead>
      <tbody>
        { 
          cities.map(product => (
            <tr key={product.id}>
              <td>{product.name}</td>
              <td>{product.lon}</td>
              <td>{product.lat}</td>
              <td>
                <Link className="btn-action" to={"/admin/product/edit/"+product.id}>✏️</Link>
                <span className="btn-action" onClick={() => {if(window.confirm('Vuoi rimuovere il prodotto?')){removeProduct(product.id)}}}>🗑</span>  
              </td>
            </tr>
          )) 
        }
      </tbody>
    </Table>

  );
}

export default TableList;
