import React, { useState } from 'react'
import { Form, Button, Col} from 'react-bootstrap';

const CityForm = ( { handleSubmit, form, setForm } ) => {
  const [errors, setErrors] = useState({})

  const validateForm = (form) => {
    //validate handle react / react hook / yup / ..
    for (const [key, value] of Object.entries(form)) {
      if(!value){
        setErrors({ [key]: "required*"})
        return false
      } 
    }
    return true
  }

  const handleChange = (target) => {
    setForm({
      ...form,
      [target.name]: target.value
    })
  }

  const submitForm = e => {
    e.preventDefault();
    if(validateForm(form)){
      handleSubmit(e, {...form})
    }
  }

  return(
    <Form style={{marginTop: '50px'}}>
      <Form.Row>
        <Form.Group as={Col} md={4} xs={12} controlId="GridName">
          <Form.Label>Nome Città*</Form.Label>
          <Form.Control placeholder="Nome città" name="name" value={form.name} onChange={e => handleChange(e.target)} />
          <p>{errors.name}</p>
        </Form.Group>
        <Form.Group as={Col} md={4} xs={12} controlId="GridLat">
          <Form.Label>Latitudine*</Form.Label>
          <Form.Control placeholder="Latitudine" name="lat" value={form.lat} disabled />
          <p>{errors.lat}</p>
        </Form.Group>
        <Form.Group as={Col} md={4} xs={12} controlId="GridLon">
          <Form.Label>Longitudine*</Form.Label>
          <Form.Control placeholder="Longitudine" name="lon" value={form.lon} disabled />
          <p>{errors.lon}</p>
        </Form.Group>
      </Form.Row>
      <Button variant="primary" type="button" onClick={submitForm}>
        Salva
      </Button>
    </Form>  
    )

}

export default CityForm
