import React, { useEffect, useState } from 'react'
import AnimationText from '../components/AnimationText'
import Forecasts from '../components/Forecasts'
import GetLocation from '../components/GetLocation'
import { getForecast } from '../api/openweather'

const HomePage = () => {
  const {latitude, longitude, error} = GetLocation();
  const [weather, setWeather] = useState({})
  const [weatherPresent, setWeatherPresent] = useState(false)

  const fetchDataForecast = async (data) => {
    const resWeather = await getForecast(data)
    setWeather(resWeather)
    setWeatherPresent(true)
  }

  if(latitude && longitude && weatherPresent == false){
    fetchDataForecast({
      lat: latitude,
      long: longitude
    })
  }

  const Component = () => {
    return (weather) ?
      <h1>{weather.name}</h1>
      : 
      <p>loading</p> 
  }

  return (
    <div>
      <Component />
      {/* <AnimationText text='Posizione attuale' /> */}
      {/* <Forecasts /> */}
    </div>
  )
}
export default HomePage
