import React, { useEffect, useState } from 'react'
import { Form, Button, Col} from 'react-bootstrap';
import { Hint } from 'react-autocomplete-hint';
import { getJsonCities } from '../../api/cities'

const CityForm = ( { handleSubmit, form, setForm } ) => {
  const [errors, setErrors] = useState({})
  const [hintData, setHintData] = useState([])
  const [disabled, setDisabled] = useState(false)

  const insertCoord = (coordinate) => {
    form.name = fomr.name
    form.lat = coordinate.lat
    form.lon = coordinate.lon
    setForm(form)
  }

  const getData = async (name) => {
    const resJsonCities = await getJsonCities(name)
    var hintArray = []
    resJsonCities.map(a => {
      hintArray.push(a.name)
      insertCoord(a.coord)
    })
    setHintData(hintArray)
  }

  const validateForm = (form) => {
    //validate handle react / react hook / yup / ..
    for (const [key, value] of Object.entries(form)) {
      if(!value){
        setErrors({ [key]: "required*"})
        return false
      } 
    }

    return true
  }

  const handleClick = (target) => {
    setForm({
      ...form,
      [target.name]: target.value
    })
  }

  const handleChange = async (target) => {
    if(target.name == 'name'){
      setForm({
        ...form,
        ['name']: target.value
      })

      if(target.value.length > 3){
        setDisabled(true)
        await getData(target.value)
        setDisabled(false)
      } else {
        form.lat = ''
        form.lon = ''
        setForm(form)
      }
    }
    setForm({
      ...form,
      [target.name]: target.value
    })
  }

  const submitForm = e => {
    e.preventDefault();
    if(validateForm(form)){
      handleSubmit(e, {...form})
    }
  }

  return(
    <Form style={{marginTop: '50px'}}>
      <Form.Row>
        <Form.Group as={Col} md={4} xs={12} controlId="GridName">
          <Form.Label>Nome Città*</Form.Label>
          <Hint options={hintData} allowTabFill>
            <input className='input-with-hint form-control'
              value={form.name}
              disabled={disabled}
              name="name"
              onChange={e => handleChange(e.target)}   
              onClick={e => handleClick(e.target)}   
              />
          </Hint>

          <p>{errors.name}</p>
        </Form.Group>
        <Form.Group as={Col} md={4} xs={12} controlId="GridLat">
          <Form.Label>Latitudine*</Form.Label>
          <Form.Control placeholder="Latitudine" name="lat" value={form.lat} 
          disabled
          onChange={e => handleChange(e.target)}
           />
          <p>{errors.lat}</p>
        </Form.Group>
        <Form.Group as={Col} md={4} xs={12} controlId="GridLon">
          <Form.Label>Longitudine*</Form.Label>
          <Form.Control placeholder="Longitudine" name="lon" value={form.lon} 
          disabled
          onChange={e => handleChange(e.target)}
           />
          <p>{errors.lon}</p>
        </Form.Group>
      </Form.Row>
      <Button variant="primary" type="button" onClick={submitForm}>
        Salva
      </Button>
    </Form>  
    )

}

export default CityForm
