import React, { useEffect, useState } from 'react'
import AnimationText from '../components/AnimationText'
import Forecasts from '../components/Forecasts'
import GetLocation from '../components/GetLocation'
import { getWeather } from '../api/openweather'

const HomePage = () => {
  const {latitude, longitude, error} = GetLocation();
  const [weather, setWeather] = useState({})

  const fetchDataWeather = async (data) => {
    const resWeather = await getWeather(data)
    setWeather(resWeather)
    console.log(weather)
  }

  if(latitude && longitude && Object.keys(weather).length === 0){
    fetchDataWeather({
      lat: latitude,
      long: longitude
    })
  }

  const Component = () => {
    return (weather) ?
      <h1>{weather.name}</h1>
      : 
      <p>loading</p> 
  }

  return (
    <div>
      <Component />
      {/* <AnimationText text='Posizione attuale' /> */}
      {/* <Forecasts /> */}
    </div>
  )
}
export default HomePage
